from typing import List

class Solution:
    def canMakeArithmeticProgression(self, arr: List[int]) -> bool:
        arr.sort()
        diff = None
        for i in range(1, len(arr)):
            if i == 1:
                diff = arr[i] - arr[i-1]
            elif arr[i] - arr[i-1] != diff:
                return False
        return True

