package main

import (
	"fmt"
	"math"
)

func maxScore(s string) int {
	ones := 0
	zeros := 0
	best := int(math.Inf(-1))
	N := len(s)
	for i := 0; i < N-1; i++ {
		if string(s[i]) == "0" {
			zeros++
		} else {
			ones++
		}
		best = max(best, zeros-ones)
	}
	if string(s[N-1]) == "1" {
		ones++
	}
	return best + ones
}

func main() {
	data := []string{
                "011101",
                "00111",
                "1111",
                "00000",
                "1010101010101010101010",
                "010101010101010101010101",
                "01",
                "10",
                "",
	        }
	for _, s := range data {
		fmt.Printf("s = %s\n", s)
		r := maxScore(s)
		fmt.Printf("r = %d\n", r)
		fmt.Println("======================")
	}
}

