class Solution:
    def minOperations(self, s: str) -> int:
        size = len(s)
        str1 = ('10'*size)[:size]
        str2 = ('01'*size)[:size]
        diff1, diff2 = 0, 0
        for a, b, c in zip(s, str1, str2):
            if a != b:
                diff1 += 1
            if a != c:
                diff2 += 1
        return min(diff1, diff2)

# Main section
for s in [
            '0000000',
            '10000',
            '00001',
            '11001100',
            '10001',
            '01001',
            '0001101101011010011100000110100000101110110000001001011001011111000011111101101001100101100110111010',
            '',
         ]:
    print(f's = {s}')
    sol = Solution()
    r = sol.minOperations(s)
    print(f'r = {r}')
    print('===========================')

