from typing import List
from collections import Counter

class Solution:
    def minCost(self, nums: List[int], k: int) -> int:
        N = len(nums)
        min_val = float('inf')
        for i in range(0, N+1):
            print(f'\t{i}, {nums[:i+1]}, {nums[i+1:]}')
            sa1 = nums[:i+1]
            sa2 = nums[i+1:]
            c1 = Counter(sa1)
            c2 = Counter(sa2)
            iv1 = k + sum([i for i in c1.values() if i > 1])
            if len(sa2) == 0:
                iv2 = 0
            else:
                iv2 = k + sum([i for i in c2.values() if i > 1])
            min_val = min(min_val, iv1 + iv2)
            print(f'\t{iv1}, {iv2}, {iv1 + iv2}, {min_val}')
            print('=====')
        return min_val

