from typing import List
from collections import defaultdict

class Solution:
    def distinctNames(self, ideas: List[str]) -> int:
        hsh = defaultdict(list)
        for i in ideas:
            hsh[i[0]] += [i[1:]]
        hsh_keys = list(hsh.keys())
        cnt = 0
        for i in range(0, len(hsh_keys)):
            x = hsh_keys[i]
            for j in range(i+1, len(hsh_keys)):
                y = hsh_keys[j]
                common_count = len(set(hsh[x]).intersection(set(hsh[y])))
                cnt += 2 * (len(hsh[x]) - common_count) * (len(hsh[y]) - common_count)
        return cnt

