from typing import List
class Solution:
    def findScore(self, nums: List[int]) -> int:
        N = len(nums)
        arr = sorted([(v, i) for i, v in enumerate(nums)])
        marked = [0] * N
        score = 0
        for v, i in arr:
            if marked[i] == 0:
                score += v
                marked[i] = 1
                if i > 0:
                    marked[i-1] = 1
                if i < N - 1:
                    marked[i+1] = 1
        return score

