from typing import List

class Solution:
    def largestMagicSquare(self, grid: List[List[int]]) -> int:
        def is_magic_square(r1, c1, r2, c2):
            total = sum(grid[r1][c1:c2+1])
            for r in range(r1+1, r2+1):
                if sum(grid[r][c1:c2+1]) != total:
                    return False
            for c in range(c1, c2+1):
                curr = 0
                for r in range(r1, r2+1):
                    curr += grid[r][c]
                if curr != total:
                    return False
            diag1 = 0
            c = c1
            for r in range(r1, r2+1):
                diag1 += grid[r][c]
                c += 1
            if diag1 != total:
                return False
            diag2 = 0
            c = c2
            for r in range(r1, r2+1):
                diag2 += grid[r][c]
                c -= 1
            if diag2 != total:
                return False
            return True
        def within_bounds(r, c):
            return 0 <= r < rows and 0 <= c < cols
        rows = len(grid)
        cols = len(grid[0])
        res = 1
        for r in range(rows):
            for c in range(cols):
                delta = 1
                r2, c2 = r + delta, c + delta
                while within_bounds(r2, c2):
                    if is_magic_square(r, c, r2, c2):
                        res = max(res, delta + 1)
                    delta += 1
                    r2, c2 = r + delta, c + delta
        return res

