from collections import Counter
import sys
class Solution:
    def longestSubstring(self, s: str, k: int) -> int:
        '''
           Divide and Conquer
           'abcxxxabc',    k = 3 => ans = 3
           'abcxxxabcabc', k = 3 => ans = 12
        '''
        def solve(s):
            cntr = Counter(s)
            if all([v >= k for v in cntr.values()]):
                self.res = max(self.res, len(s))
                return
            for i, v in enumerate(s):
                if cntr[v] < k:
                    ind = i
                    break
            solve(s[:ind])
            solve(s[ind+1:])
        self.res = 0
        solve(s)
        return self.res

# Main section
#sys.setrecursionlimit(1000000)
for s, k in [
               ('aaabb', 3),
               ('ababbc', 2),
               ('abcxxxabc', 3),
               ('abcxxxabcabc', 3),
               ('rhjakrpjkmiynkqsofamwineudrumdbgluwwfvjhyasaeknhfjqytyhcofadtebcvmiewsxcwgogyfcshdlwvbauajczlkaznyer', 3),
               ('', 3),
               ('', 94),
            ]:
    print(f's, k = {s}, {k}')
    sol = Solution()
    r = sol.longestSubstring(s, k)
    print(f'r = {r}')
    print('===================')


