package main

import "fmt"

func gcd(a, b int) int {
    if b == 0 {
        return a
    }
    return gcd(b, a % b)
}

func minimumSplits(nums []int) int {
    res := 1
    g := nums[0]
    for i := 1; i < len(nums); i++ {
        v := gcd(g, nums[i])
        if v == 1 {
            res++
            g = nums[i]
        } else {
            g = v
        }
    }
    return res
}

