from typing import List
import heapq

class Solution:
    def minimumEffortPath(self, heights: List[List[int]]) -> int:
        def dfs(r, c, k, visited):
            #print(r, c, k, visited)
            if r == rows - 1 and c == cols - 1:
                return True
            visited[r][c] = True
            for dr, dc in directions:
                rnew = r + dr
                cnew = c + dc
                if 0 <= rnew < rows and 0 <= cnew < cols:
                    if not visited[rnew][cnew] and abs(heights[rnew][cnew] - heights[r][c]) <= k and dfs(rnew, cnew, k, visited):
                        return True
            return False
        # Binary Search + DFS
        rows = len(heights)
        cols = len(heights[0])
        directions = [(0,1),(1,0),(0,-1),(-1,0)]
        left, right = 0, 10**6
        while left <= right:
            mid = (left + right) // 2
            visited = [[False for _ in range(cols)] for _ in range(rows)]
            canTravel = dfs(0, 0, mid, visited)
            #print(canTravel)
            if canTravel:
                right = mid - 1
            else:
                left = mid + 1
        return left

