class Solution:
    def minimumSteps(self, s: str) -> int:
        '''
            1000001001
            1000000011
        '''
        s += '1'
        N = len(s)
        right, left = N - 1, N - 2
        res = 0
        while left >= 0:
            if s[left] == '1':
                res += right - left - 1
                right -= 1
            left -= 1
        return res

# Main section
for s in [
            "101",
            "100",
            "0111",
            "1000001001",
            "1011101010101111101011111000001111101110100011000111110100000011001000111011110101101111000111111111",
            "",
            "",
         ]:
    print(f's = {s}')
    sol = Solution()
    r = sol.minimumSteps(s)
    print(f'r = {r}')
    print('=====================')


